/**
 * Job Posting Manager Component
 * Handles creation, editing, cloning, and management of job postings
 */

import React, { useState, useEffect } from 'react';
import { useToast } from '../Toast';
import { getStatusErrorMessage } from '../../utils/errorHandler';
import { jobsAPI } from '../../services/api';
import dataService from '../../services/dataService';
import resumeAnalyzer from '../../services/resumeAnalyzer';

const JobPostingManager = () => {
  const { showSuccess, showError, showInfo } = useToast();
  const [activeView, setActiveView] = useState('list'); // 'list', 'create', 'edit', 'candidates'
  const [jobs, setJobs] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [selectedJobCandidates, setSelectedJobCandidates] = useState([]);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    requirements: '',
    required_skills: '',
    location: '',
    experience_level: 'mid',
    employment_type: 'full-time',
    salary_range: '',
    company_name: '',
    application_deadline: ''
  });

  useEffect(() => {
    loadJobs();
  }, []);

  const loadJobs = async () => {
    try {
      setIsLoading(true);
      // Load jobs from data service
      const jobsData = await dataService.getAllJobs();
      setJobs(jobsData);
    } catch (error) {
      console.error('Error loading jobs:', error);
      showError('Failed to load job postings');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await handleSave();
  };

  const handleSave = async (isDraft = false) => {
    try {
      setIsLoading(true);

      // Validate required fields
      if (!formData.title || !formData.description) {
        showError('Please fill in required fields (Title and Description)');
        return;
      }

      const jobData = {
        title: formData.title,
        description: formData.description,
        requirements: formData.requirements,
        required_skills: formData.required_skills.split(',').map(skill => skill.trim()).filter(skill => skill),
        location: formData.location,
        experience_level: formData.experience_level,
        employment_type: formData.employment_type,
        salary_range: formData.salary_range,
        company_name: formData.company_name,
        application_deadline: formData.application_deadline,
        status: formData.status || 'draft'
      };

      console.log('Submitting job data:', jobData);

      let response;
      if (selectedJob) {
        // Update existing job
        response = await dataService.updateJob(selectedJob._id, jobData);
      } else {
        // Create new job
        response = await dataService.createJob(jobData);
      }

      if (response && response.success) {
        showSuccess(selectedJob ? 'Job updated successfully!' : 'Job created successfully!');
        setActiveView('list');
        loadJobs();
        resetForm();
      } else {
        showError('Failed to save job posting');
      }
    } catch (error) {
      console.error('Job save error:', error);
      const errorMessage = getStatusErrorMessage(error, 'Failed to save job posting');
      showError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const loadJobCandidates = async (jobId) => {
    try {
      setIsLoading(true);
      // Load candidates for specific job from data service
      const candidates = await dataService.getCandidatesForJob(jobId);
      setSelectedJobCandidates(candidates);
    } catch (error) {
      console.error('Error loading candidates:', error);
      showError('Failed to load candidates');
    } finally {
      setIsLoading(false);
    }
  };

  const handleViewCandidates = (job) => {
    setSelectedJob(job);
    setActiveView('candidates');
    loadJobCandidates(job._id);
  };

  const handleClone = (job) => {
    setFormData({
      title: `${job.title} (Copy)`,
      description: job.description,
      requirements: job.requirements || '',
      required_skills: Array.isArray(job.required_skills) ? job.required_skills.join(', ') : job.required_skills || '',
      location: job.location,
      experience_level: job.experience_level,
      employment_type: job.employment_type,
      salary_range: job.salary_range,
      company_name: job.company_name,
      application_deadline: job.application_deadline || '',
      status: 'draft'
    });
    setSelectedJob(null);
    setActiveView('create');
    showInfo('Job cloned. Make your changes and save.');
  };

  const handleEdit = (job) => {
    setSelectedJob(job);
    setFormData({
      title: job.title,
      description: job.description,
      requirements: job.requirements || '',
      required_skills: Array.isArray(job.required_skills) ? job.required_skills.join(', ') : job.required_skills || '',
      location: job.location,
      experience_level: job.experience_level,
      employment_type: job.employment_type,
      salary_range: job.salary_range,
      company_name: job.company_name,
      application_deadline: job.application_deadline || '',
      status: job.status || 'draft'
    });
    setActiveView('edit');
  };

  const handleDelete = async (jobId) => {
    if (!window.confirm('Are you sure you want to delete this job posting?')) {
      return;
    }

    try {
      setIsLoading(true);
      const response = await dataService.deleteJob(jobId);
      if (response && response.success) {
        showSuccess('Job posting deleted successfully');
        loadJobs();
      } else {
        showError('Failed to delete job posting');
      }
    } catch (error) {
      console.error('Error deleting job:', error);
      showError('Failed to delete job posting');
    } finally {
      setIsLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      requirements: '',
      required_skills: '',
      location: '',
      experience_level: 'mid',
      employment_type: 'full-time',
      salary_range: '',
      company_name: '',
      application_deadline: '',
      status: 'draft'
    });
    setSelectedJob(null);
  };

  // AI Analysis Functions
  const handleResumeUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploadingResume(true);
    setAiAnalysisData(null);
    setSkillMatchResults([]);

    try {
      // Analyze resume for skills
      const analysis = await resumeAnalyzer.analyzeResume(file);

      if (analysis.success && analysis.skills.length > 0) {
        setAiAnalysisData(analysis);

        // Find matching jobs for the extracted skills
        const allJobs = await dataService.getAllJobs();
        const matchResults = findMatchingJobs(analysis.skills, allJobs);
        setSkillMatchResults(matchResults);

        showSuccess(`Resume analyzed successfully! Found ${analysis.totalSkillsFound} skills and ${matchResults.length} matching jobs.`);
      } else {
        showError(analysis.error || 'Failed to extract skills from resume');
      }
    } catch (error) {
      console.error('Resume analysis error:', error);
      showError('Failed to analyze resume. Please try again.');
    } finally {
      setUploadingResume(false);
      event.target.value = ''; // Reset file input
    }
  };

  const findMatchingJobs = (candidateSkills, allJobs) => {
    return allJobs.map(job => {
      const jobSkills = job.required_skills || [];

      // Calculate skill match
      const matchingSkills = candidateSkills.filter(candidateSkill =>
        jobSkills.some(jobSkill =>
          candidateSkill.toLowerCase().includes(jobSkill.toLowerCase()) ||
          jobSkill.toLowerCase().includes(candidateSkill.toLowerCase())
        )
      );

      const missingSkills = jobSkills.filter(jobSkill =>
        !candidateSkills.some(candidateSkill =>
          candidateSkill.toLowerCase().includes(jobSkill.toLowerCase()) ||
          jobSkill.toLowerCase().includes(candidateSkill.toLowerCase())
        )
      );

      const matchPercentage = jobSkills.length > 0 
        ? Math.round((matchingSkills.length / jobSkills.length) * 100)
        : 0;

      return {
        ...job,
        matchPercentage,
        matchingSkills,
        missingSkills,
        totalRequiredSkills: jobSkills.length
      };
    })
    .filter(job => job.matchPercentage > 0) // Only show jobs with some match
    .sort((a, b) => b.matchPercentage - a.matchPercentage); // Sort by match percentage
  };

  // Career Counseling Analysis Function
  const generateCareerCounselingFeedback = (analysisData, matchResults) => {
    const skills = analysisData.skills || [];
    const skillCount = skills.length;

    // Analyze skill categories
    const techSkills = skills.filter(skill => 
      ['JavaScript', 'Python', 'React', 'Node.js', 'Java', 'C++', 'HTML', 'CSS', 'SQL', 'Git', 'Docker', 'AWS'].includes(skill)
    );

    const softSkills = skills.filter(skill => 
      ['Leadership', 'Communication', 'Teamwork', 'Problem Solving', 'Project Management', 'Time Management'].includes(skill)
    );

    const frameworks = skills.filter(skill => 
      ['React', 'Angular', 'Vue.js', 'Django', 'Flask', 'Express.js', 'Spring', 'Laravel'].includes(skill)
    );

    const cloudSkills = skills.filter(skill => 
      ['AWS', 'Azure', 'Google Cloud', 'Docker', 'Kubernetes', 'Terraform'].includes(skill)
    );

    // Generate insights
    const insights = [];
    const recommendations = [];
    const careerPaths = [];

    // Skill diversity analysis
    if (skillCount >= 15) {
      insights.push("🎯 Strong skill diversity - You have a comprehensive technical toolkit");
    } else if (skillCount >= 10) {
      insights.push("📚 Good skill foundation - Consider expanding into emerging technologies");
    } else {
      insights.push("🚀 Growing skill set - Focus on building depth in core technologies");
    }

    // Technical depth analysis
    if (techSkills.length >= 8) {
      insights.push("💻 Excellent technical depth across multiple domains");
      careerPaths.push("Senior Developer", "Technical Lead", "Solutions Architect");
    } else if (techSkills.length >= 5) {
      insights.push("⚡ Solid technical foundation with room for specialization");
      careerPaths.push("Mid-Level Developer", "Full Stack Developer");
    } else {
      insights.push("🌱 Developing technical skills - Focus on mastering fundamentals");
      careerPaths.push("Junior Developer", "Entry-Level Engineer");
    }

    // Leadership potential
    if (softSkills.length >= 3) {
      insights.push("👥 Strong leadership potential with soft skills");
      recommendations.push("Consider team lead or management roles");
      careerPaths.push("Team Lead", "Engineering Manager");
    }

    // Framework expertise
    if (frameworks.length >= 3) {
      insights.push("🏗️ Framework versatility shows adaptability");
      recommendations.push("Leverage framework knowledge for full-stack roles");
    } else if (frameworks.length === 0) {
      recommendations.push("Learn popular frameworks to increase marketability");
    }

    // Cloud readiness
    if (cloudSkills.length >= 2) {
      insights.push("☁️ Cloud-ready profile aligns with industry trends");
      careerPaths.push("Cloud Engineer", "DevOps Engineer");
    } else {
      recommendations.push("Develop cloud skills - high demand in current market");
    }

    // Market alignment analysis
    const avgMatchScore = matchResults.length > 0 
      ? Math.round(matchResults.reduce((sum, job) => sum + job.matchPercentage, 0) / matchResults.length)
      : 0;

    if (avgMatchScore >= 80) {
      insights.push("🎯 Excellent market alignment - Your skills match current job demands");
    } else if (avgMatchScore >= 60) {
      insights.push("📈 Good market fit with opportunities for improvement");
    } else {
      insights.push("💡 Skill gap identified - Focus on in-demand technologies");
    }

    // Specific recommendations based on skill gaps
    const allJobSkills = matchResults.flatMap(job => job.missingSkills);
    const skillGaps = {};
    allJobSkills.forEach(skill => {
      skillGaps[skill] = (skillGaps[skill] || 0) + 1;
    });

    const topGaps = Object.entries(skillGaps)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5)
      .map(([skill]) => skill);

    if (topGaps.length > 0) {
      recommendations.push(`Priority skills to learn: ${topGaps.slice(0, 3).join(', ')}`);
    }

    // Career progression advice
    if (matchResults.some(job => job.experience_level === 'senior' && job.matchPercentage >= 70)) {
      recommendations.push("Ready for senior-level positions in your domain");
    } else if (matchResults.some(job => job.experience_level === 'mid' && job.matchPercentage >= 80)) {
      recommendations.push("Strong candidate for mid-level roles, focus on leadership skills for advancement");
    }

    return {
      insights,
      recommendations,
      careerPaths: [...new Set(careerPaths)], // Remove duplicates
      skillBreakdown: {
        technical: techSkills.length,
        soft: softSkills.length,
        frameworks: frameworks.length,
        cloud: cloudSkills.length,
        total: skillCount
      },
      marketAlignment: avgMatchScore,
      topSkillGaps: topGaps.slice(0, 3)
    };
  };

  const renderJobList = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Job Postings</h2>
        <div className="flex space-x-3">
          <button
            onClick={() => {
              resetForm();
              setActiveView('create');
            }}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 flex items-center"
          >
            <span className="mr-2">+</span>
            Create New Job
          </button>
        </div>
      </div>

      {isLoading ? (
        <div className="flex items-center justify-center p-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Job Title
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Location
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Applications
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Posted
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {jobs.map((job) => (
                <tr key={job._id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{job.title}</div>
                    <div className="text-sm text-gray-500">{job.company_name}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{job.location}</div>
                    <div className="text-sm text-gray-500">{job.employment_type}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      job.status === 'open' ? 'bg-green-100 text-green-800' :
                      job.status === 'draft' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {job.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    <button
                      onClick={() => handleViewCandidates(job)}
                      className="text-indigo-600 hover:text-indigo-900 font-medium"
                    >
                      {job.applications_count || 0} applications
                    </button>
                  </td>
