                        {job.salary_range && ` • ${job.salary_range}`}
                      </div>

                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleEdit(job)}
                          className="px-3 py-1 bg-indigo-600 text-white rounded text-sm hover:bg-indigo-700"
                        >
                          Edit Job
                        </button>
                        <button
                          onClick={() => handleViewCandidates(job)}
                          className="px-3 py-1 bg-green-600 text-white rounded text-sm hover:bg-green-700"
                        >
                          View Candidates
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Recommendations */}
          {aiAnalysisData && skillMatchResults.length > 0 && (
            <div className="bg-blue-50 rounded-lg p-6">
              <h3 className="text-lg font-semibold text-blue-900 mb-3">
                💡 Recommendations
              </h3>
              <div className="space-y-2 text-blue-800">
                <p>• Consider reaching out to candidates with {Math.round(skillMatchResults.reduce((sum, job) => sum + job.matchPercentage, 0) / skillMatchResults.length)}% average skill match</p>
                <p>• Focus on jobs requiring skills that appear frequently in uploaded resumes</p>
                <p>• Create targeted job postings for high-demand skills in your candidate pool</p>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );

  switch (activeView) {
    case 'create':
    case 'edit':
      return renderJobForm();
    case 'candidates':
      return renderCandidatesView();
    case 'ai-analysis':
      return renderAiAnalysisView();
    default:
      return renderJobList();
  }
};

export default JobPostingManager;
