/**
 * Job Posting Manager Component
 * Handles creation, editing, cloning, and management of job postings
 */

import React, { useState, useEffect } from 'react';
import { useToast } from '../Toast';
import { getStatusErrorMessage } from '../../utils/errorHandler';
import { jobsAPI } from '../../services/api';
import dataService from '../../services/dataService';
import resumeAnalyzer from '../../services/resumeAnalyzer';

const JobPostingManager = () => {
  const { showSuccess, showError, showInfo } = useToast();
  const [activeView, setActiveView] = useState('list'); // 'list', 'create', 'edit', 'candidates', 'ai-analysis'
  const [jobs, setJobs] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [selectedJobCandidates, setSelectedJobCandidates] = useState([]);

  // AI Analysis states
  const [aiAnalysisData, setAiAnalysisData] = useState(null);
  const [uploadingResume, setUploadingResume] = useState(false);
  const [skillMatchResults, setSkillMatchResults] = useState([]);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    requirements: '',
    required_skills: '',
    location: '',
    experience_level: 'mid',
    employment_type: 'full-time',
    salary_range: '',
    company_name: '',
    application_deadline: ''
  });

  useEffect(() => {
    loadJobs();
  }, []);

  const loadJobs = async () => {
    try {
      setIsLoading(true);
      // Load jobs from data service
      const jobsData = await dataService.getAllJobs();
      setJobs(jobsData);
    } catch (error) {
      console.error('Error loading jobs:', error);
      showError('Failed to load job postings');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await handleSave();
  };

  const handleSave = async (isDraft = false) => {
    try {
      setIsLoading(true);

      // Validate required fields
      if (!formData.title || !formData.description) {
        showError('Please fill in required fields (Title and Description)');
        return;
      }

      const jobData = {
        title: formData.title,
        description: formData.description,
        requirements: formData.requirements,
        required_skills: formData.required_skills.split(',').map(skill => skill.trim()).filter(skill => skill),
        location: formData.location,
        experience_level: formData.experience_level,
        employment_type: formData.employment_type,
        salary_range: formData.salary_range,
        company_name: formData.company_name,
        application_deadline: formData.application_deadline,
        status: formData.status || 'draft'
      };

      console.log('Submitting job data:', jobData);

      let response;
      if (selectedJob) {
        // Update existing job
        response = await dataService.updateJob(selectedJob._id, jobData);
      } else {
        // Create new job
        response = await dataService.createJob(jobData);
      }

      if (response && response.success) {
        showSuccess(selectedJob ? 'Job updated successfully!' : 'Job created successfully!');
        setActiveView('list');
        loadJobs();
        resetForm();
      } else {
        showError('Failed to save job posting');
      }
    } catch (error) {
      console.error('Job save error:', error);
      const errorMessage = getStatusErrorMessage(error, 'Failed to save job posting');
      showError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const loadJobCandidates = async (jobId) => {
    try {
      setIsLoading(true);
      // Load candidates for specific job from data service
      const candidates = await dataService.getCandidatesForJob(jobId);
      setSelectedJobCandidates(candidates);
    } catch (error) {
      console.error('Error loading candidates:', error);
      showError('Failed to load candidates');
    } finally {
      setIsLoading(false);
    }
  };

  const handleViewCandidates = (job) => {
    setSelectedJob(job);
    setActiveView('candidates');
    loadJobCandidates(job._id);
  };

  const handleClone = (job) => {
    setFormData({
      title: `${job.title} (Copy)`,
      description: job.description,
      requirements: job.requirements || '',
      required_skills: Array.isArray(job.required_skills) ? job.required_skills.join(', ') : job.required_skills || '',
      location: job.location,
      experience_level: job.experience_level,
      employment_type: job.employment_type,
      salary_range: job.salary_range,
      company_name: job.company_name,
      application_deadline: job.application_deadline || '',
      status: 'draft'
    });
    setSelectedJob(null);
    setActiveView('create');
    showInfo('Job cloned. Make your changes and save.');
  };

  const handleEdit = (job) => {
    setSelectedJob(job);
    setFormData({
      title: job.title,
      description: job.description,
      requirements: job.requirements || '',
      required_skills: Array.isArray(job.required_skills) ? job.required_skills.join(', ') : job.required_skills || '',
      location: job.location,
      experience_level: job.experience_level,
      employment_type: job.employment_type,
      salary_range: job.salary_range,
      company_name: job.company_name,
      application_deadline: job.application_deadline || '',
      status: job.status || 'draft'
    });
    setActiveView('edit');
  };

  const handleDelete = async (jobId) => {
    if (!window.confirm('Are you sure you want to delete this job posting?')) {
      return;
    }

    try {
      setIsLoading(true);
      const response = await dataService.deleteJob(jobId);
      if (response && response.success) {
        showSuccess('Job posting deleted successfully');
        loadJobs();
      } else {
        showError('Failed to delete job posting');
      }
    } catch (error) {
      console.error('Error deleting job:', error);
      showError('Failed to delete job posting');
    } finally {
      setIsLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      requirements: '',
      required_skills: '',
      location: '',
      experience_level: 'mid',
      employment_type: 'full-time',
      salary_range: '',
      company_name: '',
      application_deadline: '',
      status: 'draft'
    });
    setSelectedJob(null);
  };

  // AI Analysis Functions
  const handleResumeUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploadingResume(true);
    setAiAnalysisData(null);
    setSkillMatchResults([]);

    try {
      // Analyze resume for skills
      const analysis = await resumeAnalyzer.analyzeResume(file);

      if (analysis.success && analysis.skills.length > 0) {
        setAiAnalysisData(analysis);

        // Find matching jobs for the extracted skills
        const allJobs = await dataService.getAllJobs();
        const matchResults = findMatchingJobs(analysis.skills, allJobs);
        setSkillMatchResults(matchResults);

        showSuccess(`Resume analyzed successfully! Found ${analysis.totalSkillsFound} skills and ${matchResults.length} matching jobs.`);
      } else {
        showError(analysis.error || 'Failed to extract skills from resume');
      }
    } catch (error) {
      console.error('Resume analysis error:', error);
      showError('Failed to analyze resume. Please try again.');
    } finally {
      setUploadingResume(false);
      event.target.value = ''; // Reset file input
    }
  };

  const findMatchingJobs = (candidateSkills, allJobs) => {
    return allJobs.map(job => {
      const jobSkills = job.required_skills || [];

      // Calculate skill match
      const matchingSkills = candidateSkills.filter(candidateSkill =>
        jobSkills.some(jobSkill =>
          candidateSkill.toLowerCase().includes(jobSkill.toLowerCase()) ||
          jobSkill.toLowerCase().includes(candidateSkill.toLowerCase())
        )
      );

      const missingSkills = jobSkills.filter(jobSkill =>
        !candidateSkills.some(candidateSkill =>
          candidateSkill.toLowerCase().includes(jobSkill.toLowerCase()) ||
          jobSkill.toLowerCase().includes(candidateSkill.toLowerCase())
        )
      );

      const matchPercentage = jobSkills.length > 0 
        ? Math.round((matchingSkills.length / jobSkills.length) * 100)
        : 0;

      return {
        ...job,
        matchPercentage,
        matchingSkills,
        missingSkills,
        totalRequiredSkills: jobSkills.length
      };
    })
    .filter(job => job.matchPercentage > 0) // Only show jobs with some match
    .sort((a, b) => b.matchPercentage - a.matchPercentage); // Sort by match percentage
  };

  const renderJobList = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Job Postings</h2>
        <div className="flex space-x-3">
          <button
            onClick={() => setActiveView('ai-analysis')}
            className="bg-purple-600 text-white px-4 py-2 rounded-md hover:bg-purple-700 flex items-center"
          >
            <span className="mr-2"></span>
            AI Resume Analysis
          </button>
          <button
            onClick={() => {
              resetForm();
              setActiveView('create');
            }}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 flex items-center"
          >
            <span className="mr-2">+</span>
            Create New Job
          </button>
        </div>
      </div>

      {isLoading ? (
        <div className="flex items-center justify-center p-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Job Title
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Location
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Applications
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Posted
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {jobs.map((job) => (
                <tr key={job._id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{job.title}</div>
                    <div className="text-sm text-gray-500">{job.company_name}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{job.location}</div>
                    <div className="text-sm text-gray-500">{job.employment_type}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      job.status === 'open' ? 'bg-green-100 text-green-800' :
                      job.status === 'draft' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {job.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    <button
                      onClick={() => handleViewCandidates(job)}
                      className="text-indigo-600 hover:text-indigo-900 font-medium"
                    >
                      {job.applications_count || 0} applications
                    </button>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {new Date(job.posted_at).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                    <button
                      onClick={() => handleEdit(job)}
                      className="text-indigo-600 hover:text-indigo-900"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleViewCandidates(job)}
                      className="text-green-600 hover:text-green-900"
                    >
                      Candidates
                    </button>
                    <button
                      onClick={() => handleClone(job)}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      Clone
                    </button>
                    <button
                      onClick={() => handleDelete(job._id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
              {jobs.length === 0 && (
                <tr>
                  <td colSpan="6" className="px-6 py-8 text-center text-gray-500">
                    No job postings found. Create your first job posting to get started!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );

  const renderCandidatesView = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">
            Candidates for "{selectedJob?.title}"
          </h2>
          <p className="text-gray-600 mt-1">
            AI-matched candidates ranked by compatibility score
          </p>
        </div>
        <button
          onClick={() => setActiveView('list')}
          className="text-gray-600 hover:text-gray-900 flex items-center"
        >
           Back to Jobs
        </button>
      </div>

      {isLoading ? (
        <div className="flex items-center justify-center p-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
        </div>
      ) : (
        <div className="space-y-4">
          {selectedJobCandidates.length === 0 ? (
            <div className="bg-white rounded-lg shadow-md p-8 text-center">
              <div className="text-gray-400 text-5xl mb-4"></div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No candidates yet</h3>
              <p className="text-gray-600">
                Once candidates apply for this position, they'll appear here with AI-powered match scores.
              </p>
            </div>
          ) : (
            selectedJobCandidates.map((candidate) => (
              <div key={candidate.user_id} className="bg-white rounded-lg shadow-md p-6">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center mb-2">
                      <h3 className="text-lg font-semibold text-gray-900 mr-3">
                        {candidate.user_name}
                      </h3>
                      <div className="flex items-center">
                        <span className="text-sm font-medium text-gray-600 mr-2">Match Score:</span>
                        <div className={`px-3 py-1 rounded-full text-sm font-bold ${
                          candidate.match_score >= 90 ? 'bg-green-100 text-green-800' :
                          candidate.match_score >= 80 ? 'bg-blue-100 text-blue-800' :
                          candidate.match_score >= 70 ? 'bg-yellow-100 text-yellow-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {candidate.match_score}%
                        </div>
                      </div>
                    </div>

                    <p className="text-gray-600 mb-2">{candidate.user_email}</p>

                    <div className="mb-3">
                      <span className="text-sm font-medium text-gray-700">Location: </span>
                      <span className="text-sm text-gray-600">{candidate.location}</span>
                      <span className="text-sm font-medium text-gray-700 ml-4">Experience: </span>
                      <span className="text-sm text-gray-600 capitalize">{candidate.experience_level}</span>
                    </div>

                    <div className="mb-3">
                      <span className="text-sm font-medium text-gray-700">Skills: </span>
                      <div className="flex flex-wrap gap-1 mt-1">
                        {candidate.skills.map((skill, index) => (
                          <span
                            key={index}
                            className="inline-flex px-2 py-1 text-xs bg-indigo-100 text-indigo-800 rounded-md"
                          >
                            {skill}
                          </span>
                        ))}
                      </div>
                    </div>

                    <div className="mb-4">
                      <span className="text-sm font-medium text-gray-700">Summary: </span>
                      <p className="text-sm text-gray-600 mt-1">{candidate.resume_summary}</p>
                    </div>
                  </div>

                  <div className="ml-6 flex flex-col space-y-2">
                    <button className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 text-sm">
                      View Full Profile
                    </button>
                    <button className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 text-sm">
                      Contact Candidate
                    </button>
                    <button className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 text-sm">
                      Download Resume
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );

  const renderJobForm = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">
          {selectedJob ? 'Edit Job Posting' : 'Create New Job Posting'}
        </h2>
        <button
          onClick={() => setActiveView('list')}
          className="text-gray-600 hover:text-gray-900"
        >
           Back to List
        </button>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Basic Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Job Title *
              </label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="e.g., Senior Software Engineer"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Company Name *
              </label>
              <input
                type="text"
                name="company_name"
                value={formData.company_name}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="e.g., TechCorp Solutions"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Location *
              </label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="e.g., San Francisco, CA or Remote"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Employment Type *
              </label>
              <select
                name="employment_type"
                value={formData.employment_type}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                <option value="">Select Type</option>
                <option value="full-time">Full Time</option>
                <option value="part-time">Part Time</option>
                <option value="contract">Contract</option>
                <option value="internship">Internship</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Experience Level
              </label>
              <select
                name="experience_level"
                value={formData.experience_level}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                <option value="">Select Level</option>
                <option value="entry">Entry Level</option>
                <option value="mid">Mid Level</option>
                <option value="senior">Senior Level</option>
                <option value="executive">Executive</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Salary Range
              </label>
              <input
                type="text"
                name="salary_range"
                value={formData.salary_range}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="e.g., $80,000 - $120,000"
              />
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Job Description *
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              required
              rows={6}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="Describe the role, responsibilities, and what makes this opportunity exciting..."
            />
          </div>

          {/* Requirements */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Requirements
            </label>
            <textarea
              name="requirements"
              value={formData.requirements}
              onChange={handleInputChange}
              rows={4}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="List the required qualifications, experience, and skills..."
            />
          </div>

          {/* Required Skills */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Required Skills *
            </label>
            <input
              type="text"
              name="required_skills"
              value={formData.required_skills}
              onChange={handleInputChange}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="e.g., JavaScript, React, Node.js, Python (separate with commas)"
            />
            <p className="text-sm text-gray-500 mt-1">
              Separate skills with commas. These will be used for AI-powered candidate matching.
            </p>
          </div>

          {/* Application Deadline */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Application Deadline
              </label>
              <input
                type="date"
                name="application_deadline"
                value={formData.application_deadline}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Status
              </label>
              <select
                name="status"
                value={formData.status}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                <option value="draft">Draft</option>
                <option value="open">Open for Applications</option>
                <option value="closed">Closed</option>
              </select>
            </div>
          </div>

          {/* Form Actions */}
          <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
            <button
              type="button"
              onClick={() => setActiveView('list')}
              className="px-6 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isLoading}
              className="px-6 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
            >
              {isLoading && (
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
              )}
              {selectedJob ? 'Update Job' : 'Create Job'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );

  const renderAiAnalysisView = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">AI Resume Analysis</h2>
          <p className="text-gray-600 mt-1">
            Upload a resume to extract skills and find matching job opportunities
          </p>
        </div>
        <button
          onClick={() => setActiveView('list')}
          className="text-gray-600 hover:text-gray-900 flex items-center"
        >
           Back to Jobs
        </button>
      </div>

      {/* Resume Upload Section */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload Resume for Analysis</h3>

        <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
          <div className="text-4xl text-gray-400 mb-4"></div>

          <div className="mb-4">
            <label htmlFor="resume-upload" className="cursor-pointer">
              <div className={`inline-flex items-center px-6 py-3 rounded-md font-medium ${
                uploadingResume 
                  ? 'bg-gray-400 cursor-not-allowed' 
                  : 'bg-purple-600 hover:bg-purple-700'
              } text-white`}>
                {uploadingResume ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Analyzing Resume...
                  </>
                ) : (
                  <>
                    <span className="mr-2"></span>
                    Choose Resume File
                  </>
                )}
              </div>
            </label>
            <input
              id="resume-upload"
              type="file"
              accept=".pdf,.doc,.docx,.txt"
              onChange={handleResumeUpload}
              disabled={uploadingResume}
              className="hidden"
            />
          </div>

          <p className="text-gray-500 text-sm">
            Supported formats: PDF, DOC, DOCX, TXT
          </p>
        </div>
      </div>

      {/* Analysis Results */}
      {aiAnalysisData && (
        <div className="space-y-6">
          {/* Skills Extracted */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
               Skills Extracted ({aiAnalysisData.totalSkillsFound})
            </h3>

            <div className="flex flex-wrap gap-2">
              {aiAnalysisData.skills.map((skill, index) => (
                <span
                  key={index}
                  className="inline-flex px-3 py-1 bg-purple-100 text-purple-800 rounded-full text-sm font-medium"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>

          {/* Matching Jobs */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
               Matching Job Opportunities ({skillMatchResults.length})
            </h3>

            {skillMatchResults.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-gray-400 text-4xl mb-4"></div>
                <p className="text-gray-600">
                  No jobs found matching the extracted skills. Consider creating jobs that match these skills!
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {skillMatchResults.map((job) => (
                  <div key={job._id} className="border border-gray-200 rounded-lg p-4 hover:border-purple-300 transition-colors">
                    <div className="flex justify-between items-start mb-3">
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">{job.title}</h4>
                        <p className="text-gray-600">{job.company_name}  {job.location}</p>
                      </div>

                      {/* Match Score */}
                      <div className="text-right">
                        <div className={`inline-flex px-3 py-1 rounded-full text-sm font-bold ${
                          job.matchPercentage >= 80 ? 'bg-green-100 text-green-800' :
                          job.matchPercentage >= 60 ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {job.matchPercentage}% Match
                        </div>
                        <p className="text-xs text-gray-500 mt-1">
                          {job.matchingSkills.length} of {job.totalRequiredSkills} skills
                        </p>
                      </div>
                    </div>

                    {/* Skills Analysis */}
                    <div className="space-y-3">
                      {job.matchingSkills.length > 0 && (
                        <div>
                          <p className="text-sm font-medium text-green-700 mb-2">
                             Matching Skills ({job.matchingSkills.length})
                          </p>
                          <div className="flex flex-wrap gap-1">
                            {job.matchingSkills.map((skill, index) => (
                              <span
                                key={index}
                                className="inline-flex px-2 py-1 bg-green-100 text-green-800 rounded text-xs"
                              >
                                {skill}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}

                      {job.missingSkills.length > 0 && (
                        <div>
                          <p className="text-sm font-medium text-orange-700 mb-2">
                             Skills to Learn ({job.missingSkills.length})
                          </p>
                          <div className="flex flex-wrap gap-1">
                            {job.missingSkills.map((skill, index) => (
                              <span
                                key={index}
                                className="inline-flex px-2 py-1 bg-orange-100 text-orange-800 rounded text-xs"
                              >
                                {skill}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Job Actions */}
                    <div className="flex justify-between items-center mt-4 pt-4 border-t border-gray-100">
                      <div className="text-sm text-gray-500">
                        {job.employment_type}  {job.experience_level} level
                        {job.salary_range && `  ${job.salary_range}`}
                      </div>

                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleEdit(job)}
                          className="px-3 py-1 bg-indigo-600 text-white rounded text-sm hover:bg-indigo-700"
                        >
                          Edit Job
                        </button>
                        <button
                          onClick={() => handleViewCandidates(job)}
                          className="px-3 py-1 bg-green-600 text-white rounded text-sm hover:bg-green-700"
                        >
                          View Candidates
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Recommendations */}
          {aiAnalysisData && skillMatchResults.length > 0 && (
            <div className="bg-blue-50 rounded-lg p-6">
              <h3 className="text-lg font-semibold text-blue-900 mb-3">
                 Recommendations
              </h3>
              <div className="space-y-2 text-blue-800">
                <p> Consider reaching out to candidates with {Math.round(skillMatchResults.reduce((sum, job) => sum + job.matchPercentage, 0) / skillMatchResults.length)}% average skill match</p>
                <p> Focus on jobs requiring skills that appear frequently in uploaded resumes</p>
                <p> Create targeted job postings for high-demand skills in your candidate pool</p>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );

  switch (activeView) {
    case 'create':
    case 'edit':
      return renderJobForm();
    case 'candidates':
      return renderCandidatesView();
    default:
      return renderJobList();
  }
};

export default JobPostingManager;
